      changePosArr = this.getFixedChangePosArr(changePos, index)
    } else if (this.minRange || this.maxRange) {
      changePosArr = this.getLimitRangeChangePosArr(pos, changePos, index)
    } else {
      changePosArr[index] = changePos
    }

    this.setDotsPos(this.dotsPos.map((curPos, i) => curPos + (changePosArr[i] || 0)))
  }

  /**
   * In fixed mode, get the position of all slider changes
   *
   * @param {number} changePos Change distance of a single slider
   * @param {number} index slider index
   * @returns {DotsPosChangeArray}
   * @memberof Control
   */
  private getFixedChangePosArr(changePos: number, index: number): DotsPosChangeArray {
    this.dotsPos.forEach((originPos, i) => {
      if (i !== index) {
        const { pos: lastPos, inRange } = this.getValidPos(originPos + changePos, i)
        if (!inRange) {
          changePos =
            Math.min(Math.abs(lastPos - originPos), Math.abs(changePos)) * (changePos < 0 ? -1 : 1)
        }
      }
    })
    return this.dotsPos.map(_ => changePos)
  }

  /**
   * In minRange/maxRange mode, get the position of all slider changes
   *
   * @param {number} pos position of a single slider
   * @param {number} changePos Change distance of a single slider
   * @param {number} index slider index
   * @returns {DotsPosChangeArray}
   * @memberof Control
   */
  private getLimitRangeChangePosArr(
    pos: number,
    changePos: number,
    index: number,
  ): DotsPosChangeArray {
    const changeDots = [{ index, changePos }]
    const newChangePos = changePos
    ;[this.minRange, this.maxRange].forEach((isLimitRange?: number, rangeIndex?: number) => {
      if (!isLimitRange) {
        return false
      }
      const isMinRange = rangeIndex === 0
      const isForward = changePos > 0
      let next = 0
      if (isMinRange) {
        next = isForward ? 1 : -1
      } else {
        next = isForward ? -1 : 1
      }

      // Determine if the two positions are within the legal interval
      const inLimitRange = (pos2: number, pos1: number): boolean => {
        const diff = Math.abs(pos2 - pos1)
        return isMinRange ? diff < this.minRangeDir : diff > this.maxRangeDir
      }

      let i = index + next
      let nextPos = this.dotsPos[i]
      let curPos = pos
      while (this.isPos(nextPos) && inLimitRange(nextPos, curPos)) {
        const { pos: lastPos } = this.getValidPos(nextPos + newChangePos, i)
        changeDots.push({
          index: i,
          changePos: lastPos - nextPos,
        })
        i = i + next
        curPos = lastPos
        nextPos = this.dotsPos[i]
      }
    })

    return this.dotsPos.map((_, i) => {
      const changeDot = changeDots.filter(dot => dot.index === i)
      return changeDot.length ? changeDot[0].changePos : 0
    })
  }

  private isPos(pos: any): boolean {
    return typeof pos === 'number'
  }

  /**
   * Get a valid position by pos
   *
   * @param {number} pos
   * @param {number} index
   * @returns {{ pos: number, inRange: boolean }}
   */
  getValidPos(pos: number, index: number): { pos: number; inRange: boolean } {
    const range = this.valuePosRange[index]
    let inRange = true
    if (pos < range[0]) {
      pos = range[0]
      inRange = false
    } else if (pos > range[1]) {
      pos = range[1]
      inRange = false
    }
    return {
      pos,
      inRange,
    }
  }

  /**
   * Calculate the position of the slider by value
   *
   * @param {Value} val
   * @returns {number}
   */
  parseValue(val: Value): number {
    if (this.data) {
      val = this.data.indexOf(val)
    } else if (typeof val === 'number' || typeof val === 'string') {
      val = +val
      if (val < this.min) {
        this.emitError(ERROR_TYPE.MIN)
        return 0
      }
      if (val > this.max) {
        this.emitError(ERROR_TYPE.MAX)
        return 0
      }
      if (typeof val !== 'number' || val !== val) {
        this.emitError(ERROR_TYPE.VALUE)
        return 0
      }
      val = new Decimal(val)
        .minus(this.min)
        .divide(this.interval)
        .toNumber()
    }

    const pos = new Decimal(val).multiply(this.gap).toNumber()
    return pos < 0 ? 0 : pos > 100 ? 100 : pos
  }

  /**
   * Calculate the value by position
   *
   * @param {number} pos
   * @returns {Value}
   * @memberof Control
   */
  parsePos(pos: number): Value {
    const index = Math.round(pos / this.gap)
    return this.getValueByIndex(index)
  }

  /**
   * Determine if the location is active
   *
   * @param {number} pos
   * @returns {boolean}
   * @memberof Control
   */
  isActiveByPos(pos: number): boolean {
    return this.processArray.some(([start, end]) => pos >= start && pos <= end)
  }

  /**
   * Get each value
   *
   * @returns {Value[]}
   * @memberof Control
   */
  getValues(): Value[] {
    if (this.data) {
      return this.data
    } else {
      const values: Value[] = []
      for (let i = 0; i <= this.total; i++) {
        values.push(
          new Decimal(i)
            .multiply(this.interval)
            .plus(this.min)
            .toNumber(),
        )
      }
      return values
    }
  }

  /**
   * Calculate the distance of the range
   * @param range number
   */
  getRangeDir(range: number): number {
    return range
      ? new Decimal(range)
          .divide(
            new Decimal(this.data ? this.data.length - 1 : this.max)
              .minus(this.data ? 0 : this.min)
              .toNumber(),
          )
          .multiply(100)
          .toNumber()
      : 100
  }

  private emitError(type: ERROR_TYPE) {
    if (this.onError) {
      this.onError(type, ERROR_MSG[type])
    }
  }

  get processArray(): ProcessOption {
    if (this.process) {
      if (typeof this.process === 'function') {
        return this.process(this.dotsPos)
      } else if (this.dotsPos.length === 1) {
        return [[0, this.dotsPos[0]]]
      } else if (this.dotsPos.length > 1) {
        return [[Math.min(...this.dotsPos), Math.max(...this.dotsPos)]]
      }
    }

    return []
  }

  /**
   * The total number of values
   *
   * @type {number}
   * @memberof Control
   */
  get total(): number {
    let total = 0
    if (this.data) {
      total = this.data.length - 1
    } else {
      total = new Decimal(this.max)
        .minus(this.min)
        .divide(this.interval)
        .toNumber()
    }
    if (total - Math.floor(total) !== 0) {
      this.emitError(ERROR_TYPE.INTERVAL)
      return 0
    }
    return total
  }

  // Distance between each value
  get gap(): number {
    return 100 / this.total
  }

  private cacheRangeDir: { [key: string]: number } = {}

  // The minimum distance between the two sliders
  get minRangeDir(): number {
    if (this.cacheRangeDir[this.minRange]) {
      return this.cacheRangeDir[this.minRange]
    }
    return (this.cacheRangeDir[this.minRange] = this.getRangeDir(this.minRange))
  }

  // Maximum distance between the two sliders
  get maxRangeDir(): number {
    if (this.cacheRangeDir[this.maxRange]) return this.cacheRangeDir[this.maxRange]
    return (this.cacheRangeDir[this.maxRange] = this.getRangeDir(this.maxRange))
  }

  private getDotRange(index: number, key: 'min' | 'max', defaultValue: number): number {
    if (!this.dotOptions) {
      return defaultValue
    }

    const option = Array.isArray(this.dotOptions) ? this.dotOptions[index] : this.dotOptions
    return option && option[key] !== void 0 ? this.parseValue(option[key] as Value) : defaultValue
  }

  /**
   * Sliding range of each slider
   *
   * @type {Array<[number, number]>}
   * @memberof Control
   */
  get valuePosRange(): Array<[number, number]> {
    const dotsPos = this.dotsPos
    const valuePosRange: Array<[number, number]> = []

    dotsPos.forEach((pos, i) => {
      valuePosRange.push([
        Math.max(
          this.minRange ? this.minRangeDir * i : 0,
          !this.enableCross ? dotsPos[i - 1] || 0 : 0,
          this.getDotRange(i, 'min', 0),
        ),
        Math.min(
          this.minRange ? 100 - this.minRangeDir * (dotsPos.length - 1 - i) : 100,
          !this.enableCross ? dotsPos[i + 1] || 100 : 100,
          this.getDotRange(i, 'max', 100),
        ),
      ])
    })

    return valuePosRange
  }

  get dotsIndex(): number[] {
    return this.dotsValue.map(val => this.getIndexByValue(val))
  }
}

type Operator = '+' | '-' | '*' | '/' | '%'

/**
 * For any precise floating point calculation
 *
 * @export
 * @class Decimal
 */
export default class Decimal {
  num: number

  constructor(num: number) {
    this.num = num
  }

  decimal(num2: number, operator: Operator): this {
    const num1 = this.num
    const len1 = this.getDecimalLen(num1)
    const len2 = this.getDecimalLen(num2)
    let base = 0
    switch (operator) {
      case '+':
        base = this.getExponent(len1, len2)
        this.num = (this.safeRoundUp(num1, base) + this.safeRoundUp(num2, base)) / base
        break
      case '-':
        base = this.getExponent(len1, len2)
        this.num = (this.safeRoundUp(num1, base) - this.safeRoundUp(num2, base)) / base
        break
      case '*':
        this.num =
          this.safeRoundUp(
            this.safeRoundUp(num1, this.getExponent(len1)),
            this.safeRoundUp(num2, this.getExponent(len2)),
          ) / this.getExponent(len1 + len2)
        break
      case '/':
        base = this.getExponent(len1, len2)
        this.num = this.safeRoundUp(num1, base) / this.safeRoundUp(num2, base)
        break
      case '%':
        base = this.getExponent(len1, len2)
        this.num = (this.safeRoundUp(num1, base) % this.safeRoundUp(num2, base)) / base
        break
    }
    return this
  }

  plus(num2: number) {
    return this.decimal(num2, '+')
  }

  minus(num2: number) {
    return this.decimal(num2, '-')
  }

  multiply(num2: number) {
    return this.decimal(num2, '*')
  }

  divide(num2: number) {
    return this.decimal(num2, '/')
  }

  remainder(num2: number) {
    return this.decimal(num2, '%')
  }

  toNumber(): number {
    return this.num
  }

  private getDecimalLen(num: number): number {
    const strArr = `${num}`.split('e')
    return (`${strArr[0]}`.split('.')[1] || '').length - (strArr[1] ? +strArr[1] : 0)
  }

  private getExponent(num1: number, num2?: number): number {
    return Math.pow(10, num2 !== void 0 ? Math.max(num1, num2) : num1)
  }

  // fix: 9999999.99995 * 100000 = 999999999994.9999
  private safeRoundUp(num: number, exponent: number): number {
    return Math.round(num * exponent)
  }
}

export interface StateMap {
  [key: string]: number
}

export default class State {
  map: StateMap
  states: number = 0

  constructor(map: StateMap) {
    this.map = map
  }

  add(state: number) {
    this.states |= state
  }

  delete(state: number) {
    this.states &= ~state
  }

  toggle(state: number) {
    if (this.has(state)) {
      this.delete(state)
    } else {
      this.add(state)
    }
  }

  has(state: number): boolean {
    return !!(this.states & state)
  }
}

import { defineComponent, PropType } from 'vue'
import VueSliderDot from './vue-slider-dot.vue'
import VueSliderMark from './vue-slider-mark.vue'
import {
  Value,
  DataObject,
  MarksProp,
  Styles,
  DotOption,
  Dot,
  Direction,
  Position,
  ProcessProp,
  Process,
  TooltipProp,
  TooltipFormatter,
} from './typings'
import { getSize, getPos, getKeyboardHandleFunc, HandleFunction } from './utils'
import Decimal from './utils/decimal'
import Control, { ERROR_TYPE } from './utils/control'
import State, { StateMap } from './utils/state'

import './styles/slider.scss'

export const SliderState: StateMap = {
  None: 0,
  Drag: 1 << 1,
  Focus: 1 << 2,
}

const DEFAULT_SLIDER_SIZE = 4

export default defineComponent({
  name: 'VueSlider',
  components: {
    VueSliderDot,
    VueSliderMark,
  },
  emits: ['change', 'drag-start', 'dragging', 'drag-end', 'error', 'update:modelValue'],
  data() {
    return {
      control: null as (Control | null),
      states: new State(SliderState),
      // The width of the component is divided into one hundred, the width of each one.
      scale: 1,
      // Currently dragged slider index
      focusDotIndex: 0,
    }
  },
  props: {
    modelValue: {
      type: [Number, String, Array] as PropType<Value | Value[]>,
      default: 0 as Value,
    },

    silent: { type: Boolean, default: false },

    direction: {
      type: String as PropType<Direction>,
      default: 'ltr',
      validator: (dir: Direction) => ['ltr', 'rtl', 'ttb', 'btt'].indexOf(dir) > -1,
    },

    width: { type: [Number, String] },

    height: { type: [Number, String] },

    // The size of the slider, optional [width, height] | size
    dotSize: {
      type: [Number, Array] as PropType<number | [number, number]>,
      default: 14 as number
    },

    // whether or not the slider should be fully contained within its containing element
    contained: { type: Boolean, default: false },

    min: { type: Number, default: 0 },

    max: { type: Number, default: 100 },

    interval: { type: Number, default: 1 },

    disabled: { type: Boolean, default: false },

    clickable: { type: Boolean, default: true },

    dragOnClick: { type: Boolean, default: false },

    duration: { type: Number, default: 0.5 },

    data: {
      type: [Object, Array] as PropType<Value[] | object[] | DataObject>,
    },

    dataValue: { type: String, default: 'value' },

    dataLabel: { type: String, default: 'label' },

    lazy: { type: Boolean, default: false },

    tooltip: {
      type: String as PropType<TooltipProp>,
      default: 'active' as TooltipProp,
      validator: (val: string) => ['none', 'always', 'focus', 'hover', 'active'].indexOf(val) > -1,
    },

    tooltipPlacement: {
      type: [String, Array] as PropType<Position | Position[]>,
      validator: (val: string | string[]) => (Array.isArray(val) ? val : [val]).every(
        val => ['top', 'right', 'bottom', 'left'].indexOf(val) > -1,
      ),
    },

    tooltipFormatter: {
      type: [String, Array, Function] as PropType<TooltipFormatter | TooltipFormatter[]>,
    },

    // Keyboard control
    useKeyboard: { type: Boolean, default: true },

    // Keyboard controlled hook function
    keydownHook: {
      // eslint-disable-next-line no-unused-vars
      type: Function as PropType<(e: KeyboardEvent) => HandleFunction | boolean>,
    },

    // Whether to allow sliders to cross, only in range mode
    enableCross: { type: Boolean, default: true },

    // Whether to fix the slider interval, only in range mode
    fixed: { type: Boolean, default: false },

    // Whether to sort values, only in range mode
    // When order is false, the parameters [minRange, maxRange, fixed, enableCross] are invalid
    // e.g. When order = false, [50, 30] will not be automatically sorted into [30, 50]
    order: { type: Boolean, default: true },

    // Minimum distance between sliders, only in range mode
    minRange: { type: Number },

    // Maximum distance between sliders, only in range mode
    maxRange: { type: Number },

    marks: {
      type: [Boolean, Object, Array, Function] as PropType<MarksProp>,
      default: false as MarksProp,
    },

    process: {
      type: [Boolean, Function] as PropType<ProcessProp>,
      default: true as ProcessProp,
    },

    zoom: { type: Number },

    // If the value is true , mark will be an independent value
    included: { type: Boolean },

    // If the value is true , dot will automatically adsorb to the nearest value
    adsorb: { type: Boolean },

    hideLabel: { type: Boolean },

    // Slider settings, multiple sliders can be distinguished by array type.
    dotOptions: {
      type: [Object, Array] as PropType<DotOption | DotOption[]>,
    },

    // Set custom attributes on the slider.
    dotAttrs: { type: Object },

    railStyle: { type: Object as PropType<Styles> },

    processStyle: { type: Object as PropType<Styles> },

    dotStyle: { type: Object as PropType<Styles> },

    tooltipStyle: { type: Object as PropType<Styles> },

    stepStyle: { type: Object as PropType<Styles> },

    stepActiveStyle: { type: Object as PropType<Styles> },

    labelStyle: { type: Object as PropType<Styles> },

    labelActiveStyle: { type: Object as PropType<Styles> },
  },
  computed: {
    isHorizontal(): boolean {
      return this.direction === 'ltr' || this.direction === 'rtl'
    },
    isReverse(): boolean {
      return this.direction === 'rtl' || this.direction === 'btt'
    },
    tailSize() {
      return getSize((this.isHorizontal ? this.height : this.width) || DEFAULT_SLIDER_SIZE)
    },
    containerClasses() {
      return [
        'vue-slider',
        [`vue-slider-${this.direction}`],
        {
          'vue-slider-disabled': this.disabled,
        },
      ]
    },
    containerStyles() {
      const [dotWidth, dotHeight] = Array.isArray(this.dotSize)
        ? this.dotSize
        : [this.dotSize, this.dotSize]
      const containerWidth = this.width
        ? getSize(this.width)
        : this.isHorizontal
        ? 'auto'
        : getSize(DEFAULT_SLIDER_SIZE)
      const containerHeight = this.height
        ? getSize(this.height)
        : this.isHorizontal
        ? getSize(DEFAULT_SLIDER_SIZE)
        : 'auto'
      return {
        padding: this.contained
          ? `${dotHeight / 2}px ${dotWidth / 2}px`
          : this.isHorizontal
          ? `${dotHeight / 2}px 0`
          : `0 ${dotWidth / 2}px`,
        width: containerWidth,
        height: containerHeight,
      }
    },
    processArray(): Process[] {
      return this.control!!.processArray.map(([start, end, style], index) => {
        if (start > end) {
          ;[start, end] = [end, start]
        }
        const sizeStyleKey = this.isHorizontal ? 'width' : 'height'
        return {
          start,
          end,
          index,
          style: {
            [this.isHorizontal ? 'height' : 'width']: '100%',
            [this.isHorizontal ? 'top' : 'left']: 0,
            [this.mainDirection]: `${start}%`,
            [sizeStyleKey]: `${end - start}%`,
            transitionProperty: `${sizeStyleKey},${this.mainDirection}`,
            transitionDuration: `${this.animateTime}s`,
            ...this.processStyle,
            ...style,
          },
        }
      })
    },
    dotBaseStyle() {
      const [dotWidth, dotHeight] = Array.isArray(this.dotSize)
        ? this.dotSize
        : [this.dotSize, this.dotSize]
      let dotPos: { [key: string]: string }
      if (this.isHorizontal) {
        dotPos = {
          transform: `translate(${this.isReverse ? '50%' : '-50%'}, -50%)`,
          WebkitTransform: `translate(${this.isReverse ? '50%' : '-50%'}, -50%)`,
          top: '50%',
          [this.direction === 'ltr' ? 'left' : 'right']: '0',
        }
      } else {
        dotPos = {
          transform: `translate(-50%, ${this.isReverse ? '50%' : '-50%'})`,
          WebkitTransform: `translate(-50%, ${this.isReverse ? '50%' : '-50%'})`,
          left: '50%',
          [this.direction === 'btt' ? 'bottom' : 'top']: '0',
        }
      }
      return {
        width: `${dotWidth}px`,
        height: `${dotHeight}px`,
        ...dotPos,
      }
    },
    mainDirection(): string {
      switch (this.direction) {
        case 'ltr':
          return 'left'
        case 'rtl':
          return 'right'
        case 'btt':
          return 'bottom'
        case 'ttb':
          return 'top'
        default:
          return 'left'
      }
    },
    tooltipDirections(): Position[] {
      const dir = this.tooltipPlacement || (this.isHorizontal ? 'top' : 'left')
      if (Array.isArray(dir)) {
        return dir
      } else {
        return this.dots.map(() => dir)
      }
    },
    dots(): Dot[] {
      return this.control!!.dotsPos.map((pos, index) => ({
        pos,
        index,
        value: this.control!!.dotsValue[index],
        focus: this.states.has(SliderState.Focus) && this.focusDotIndex === index,
        disabled: this.disabled,
        style: this.dotStyle,
        ...((Array.isArray(this.dotOptions) ? this.dotOptions[index] : this.dotOptions) || {}),
      }))
    },
    animateTime(): number {
      if (this.states.has(SliderState.Drag)) {
        return 0
      }
      return this.duration
    },
    canSort(): boolean {
      return this.order && !this.minRange && !this.maxRange && !this.fixed && this.enableCross
    },
    sliderData(): undefined | Value[] {
      if (this.isObjectArrayData(this.data)) {
        return (this.data as any[]).map(obj => obj[this.dataValue])
      } else if (this.isObjectData(this.data)) {
        return Object.keys(this.data)
      } else {
        return this.data as Value[]
      }
    },
    sliderMarks(): MarksProp | undefined {
      if (this.marks) {
        return this.marks
      } else if (this.isObjectArrayData(this.data)) {
        return val => {
          const mark = { label: val }
          ;(this.data as any[]).some(obj => {
            if (obj[this.dataValue] === val) {
              mark.label = obj[this.dataLabel]
              return true
            }
            return false
          })
          return mark
        }
      } else if (this.isObjectData(this.data)) {
        return this.data
      } else {
        return void 0;
      }
    },
    sliderTooltipFormatter(): TooltipFormatter | TooltipFormatter[] | undefined {
      if (this.tooltipFormatter) {
        return this.tooltipFormatter
      } else if (this.isObjectArrayData(this.data)) {
        return val => {
          let tooltipText = '' + val
          ;(this.data as any[]).some(obj => {
            if (obj[this.dataValue] === val) {
              tooltipText = obj[this.dataLabel]
              return true
            }
            return false
          })
          return tooltipText
        }
      } else if (this.isObjectData(this.data)) {
        const data = this.data
        return val => data[val]
      } else {
        return void 0;
      }
    },
    // Slider value and component internal value are inconsistent
    isNotSync() {
      const values = this.control!!.dotsValue
      return Array.isArray(this.modelValue)
        ? this.modelValue.length !== values.length ||
            this.modelValue.some((val, index) => val !== values[index])
        : this.modelValue !== values[0]
    },
    /**
     * Get the drag range of the slider
     *
     * @private
     * @param {number} index slider index
     * @returns {[number, number]} range [start, end]
     * @memberof VueSlider
     */
    dragRange(): [number, number] {
      const prevDot = this.dots[this.focusDotIndex - 1]
      const nextDot = this.dots[this.focusDotIndex + 1]
      return [prevDot ? prevDot.pos : -Infinity, nextDot ? nextDot.pos : Infinity]
    }
  },
  watch: {
    modelValue() {
      if (this.control! && !this.states.has(SliderState.Drag) && this.isNotSync) {
        this.control!.setValue(this.modelValue)
      }
    }
  },
  methods: {
    isObjectData(data?: Value[] | object[] | DataObject): data is DataObject {
      return !!data && Object.prototype.toString.call(data) === '[object Object]'
    },
    isObjectArrayData(data?: Value[] | object[] | DataObject): data is object[] {
      return !!data && Array.isArray(data) && data.length > 0 && typeof data[0] === 'object'
    },
    bindEvent() {
      document.addEventListener('touchmove', this.dragMove, { passive: false })
      document.addEventListener('touchend', this.dragEnd, { passive: false })
      document.addEventListener('mousedown', this.blurHandle)
      document.addEventListener('mousemove', this.dragMove)
      document.addEventListener('mouseup', this.dragEnd)
      document.addEventListener('mouseleave', this.dragEnd)
      document.addEventListener('keydown', this.keydownHandle)
    },
    unbindEvent() {
      document.removeEventListener('touchmove', this.dragMove)
      document.removeEventListener('touchend', this.dragEnd)
      document.removeEventListener('mousedown', this.blurHandle)
      document.removeEventListener('mousemove', this.dragMove)
      document.removeEventListener('mouseup', this.dragEnd)
      document.removeEventListener('mouseleave', this.dragEnd)
      document.removeEventListener('keydown', this.keydownHandle)
    },
    setScale() {
      this.scale = new Decimal(
        Math.floor(this.isHorizontal ? this.$el.offsetWidth : this.$el.offsetHeight),
      )
        .multiply(this.zoom || 1)
        .divide(100)
        .toNumber()
    },
    initControl() {
      this.control! = new Control({
        value: this.modelValue,
        data: this.sliderData,
        enableCross: this.enableCross,
        fixed: this.fixed,
        max: this.max,
        min: this.min,
        interval: this.interval,
        minRange: this.minRange,
        maxRange: this.maxRange,
        order: this.order,
        marks: this.sliderMarks,
        included: this.included,
        process: this.process,
        adsorb: this.adsorb,
        dotOptions: this.dotOptions,
        onError: this.emitError,
      })
      ;[
        'data',
        'enableCross',
        'fixed',
        'max',
        'min',
        'interval',
        'minRange',
        'maxRange',
        'order',
        'marks',
        'process',
        'adsorb',
        'included',
        'dotOptions',
      ].forEach(name => {
        this.$watch(name, (val: any) => {
          if (
            name === 'data' &&
            Array.isArray(this.control!.data) &&
            Array.isArray(val) &&
            this.control!.data.length === val.length &&
            val.every((item, index) => item === (this.control!.data as Value[])[index])
          ) {
            return false
          }
          switch (name) {
            case 'data':
            case 'dataLabel':
            case 'dataValue':
              this.control!.data = this.sliderData
              break
            case 'mark':
              this.control!.marks = this.sliderMarks
              break
            default:
              ;(this.control! as any)[name] = val
          }
          if (['data', 'max', 'min', 'interval'].indexOf(name) > -1) {
            this.control!.syncDotsPos()
          }
        })
      })
    },
    syncValueByPos() {
      const values = this.control!.dotsValue
      if (this.isDiff(values, Array.isArray(this.modelValue) ? this.modelValue : [this.modelValue])) {
        const newValue = values.length === 1 ? values[0] : [...values]
        this.$emit('change', newValue, this.focusDotIndex)
        this.$emit('update:modelValue', newValue)
      }
    },
    isDiff(value1: Value[], value2: Value[]) {
      return value1.length !== value2.length || value1.some((val, index) => val !== value2[index])
    },
    emitError(type: ERROR_TYPE, message: string) {
      if (!this.silent) {
        console.error(`[VueSlider error]: ${message}`)
      }
      this.$emit('error', type, message)
    },
    dragStartOnProcess(e: MouseEvent | TouchEvent) {
      if (this.dragOnClick) {
        this.setScale()
        const pos = this.getPosByEvent(e)
        const index = this.control!.getRecentDot(pos)
        if (this.dots[index].disabled) {
          return
        }
        this.dragStart(index)
        this.control!.setDotPos(pos, this.focusDotIndex)
        if (!this.lazy) {
          this.syncValueByPos()
        }
      }
    },
    dragStart(index: number) {
      this.focusDotIndex = index
      this.setScale()
      this.states.add(SliderState.Drag)
      this.states.add(SliderState.Focus)
      this.$emit('drag-start', this.focusDotIndex)
    },
    dragMove(e: MouseEvent | TouchEvent) {
      if (!this.states.has(SliderState.Drag)) {
        return false
      }
      e.preventDefault()
      const pos = this.getPosByEvent(e)
      this.isCrossDot(pos)
      this.control!.setDotPos(pos, this.focusDotIndex)
      if (!this.lazy) {
        this.syncValueByPos()
      }
      const value = this.control!.dotsValue
      this.$emit('dragging', value.length === 1 ? value[0] : [...value], this.focusDotIndex)
    },
    // If the component is sorted, then when the slider crosses, toggle the currently selected slider index
    isCrossDot(pos: number) {
      if (this.canSort) {
        const curIndex = this.focusDotIndex
        let curPos = pos
        if (curPos > this.dragRange[1]) {
          curPos = this.dragRange[1]
          this.focusDotIndex++
        } else if (curPos < this.dragRange[0]) {
          curPos = this.dragRange[0]
          this.focusDotIndex--
        }
        if (curIndex !== this.focusDotIndex) {
          const dotVm = (this.$refs as any)[`dot-${this.focusDotIndex}`]
          if (dotVm && dotVm.$el) {
            dotVm.$el.focus()
          }
          this.control!.setDotPos(curPos, curIndex)
        }
      }
    },
    dragEnd(e: MouseEvent | TouchEvent) {
      if (!this.states.has(SliderState.Drag)) {
        return false
      }

      setTimeout(() => {
        if (this.lazy) {
          this.syncValueByPos()
        }
        if (this.included && this.isNotSync) {
          this.control!.setValue(this.modelValue)
        } else {
          // Sync slider position
          this.control!.syncDotsPos()
        }
        this.states.delete(SliderState.Drag)
        // If useKeyboard is true, keep focus status after dragging
        if (!this.useKeyboard || 'targetTouches' in e) {
          this.states.delete(SliderState.Focus)
        }
        this.$emit('drag-end', this.focusDotIndex)
      })
    },
    blurHandle(e: MouseEvent) {
      if (
        !this.states.has(SliderState.Focus) ||
        !this.$refs.container ||
        (this.$refs.container as HTMLDivElement).contains(e.target as Node)
      ) {
        return false
      }
      this.states.delete(SliderState.Focus)
    },
    clickHandle(e: MouseEvent | TouchEvent) {
      if (!this.clickable || this.disabled) {
        return false
      }
      if (this.states.has(SliderState.Drag)) {
        return
      }
      this.setScale()
      const pos = this.getPosByEvent(e)
      this.setValueByPos(pos)
    },
    focus(dot: Dot, index: number = 0) {
      if (dot.disabled) return
      this.states.add(SliderState.Focus)
      this.focusDotIndex = index
    },
    blur() {
      this.states.delete(SliderState.Focus)
    },
    getValue() {
      const values = this.control!.dotsValue
      return values.length === 1 ? values[0] : values
    },
    getIndex() {
      const indexes = this.control!.dotsIndex
      return indexes.length === 1 ? indexes[0] : indexes
    },
    setValue(value: Value | Value[]) {
      this.control!.setValue(Array.isArray(value) ? [...value] : [value])
      this.syncValueByPos()
    },
    setIndex(index: number | number[]) {
      const value = Array.isArray(index)
        ? index.map(n => this.control!.getValueByIndex(n))
        : this.control!.getValueByIndex(index)
      this.setValue(value)
    },
    setValueByPos(pos: number) {
      const index = this.control!.getRecentDot(pos)
      if (this.disabled || this.dots[index].disabled) {
        return false
      }
      this.focusDotIndex = index
      this.control!.setDotPos(pos, index)
      this.syncValueByPos()

      if (this.useKeyboard) {
        this.states.add(SliderState.Focus)
      }

      setTimeout(() => {
        if (this.included && this.isNotSync) {
          this.control!.setValue(this.modelValue)
        } else {
          this.control!.syncDotsPos()
        }
      })
    },
    keydownHandle(e: KeyboardEvent) {
      if (!this.useKeyboard || !this.states.has(SliderState.Focus)) {
        return false
      }

      const isInclude = this.included && this.marks
      const handleFunc = getKeyboardHandleFunc(e, {
        direction: this.direction,
        max: isInclude ? this.control!.markList.length - 1 : this.control!.total,
        min: 0,
        hook: this.keydownHook,
      })

      if (handleFunc) {
        e.preventDefault()
        let newIndex = -1
        let pos = 0
        if (isInclude) {
          this.control!.markList.some((mark, index) => {
            if (mark.value === this.control!.dotsValue[this.focusDotIndex]) {
              newIndex = handleFunc(index)
              return true
            }
            return false
          })
          if (newIndex < 0) {
            newIndex = 0
          } else if (newIndex > this.control!.markList.length - 1) {
            newIndex = this.control!.markList.length - 1
          }
          pos = this.control!.markList[newIndex].pos
        } else {
          newIndex = handleFunc(
            this.control!.getIndexByValue(this.control!.dotsValue[this.focusDotIndex]),
          )
          pos = this.control!.parseValue(this.control!.getValueByIndex(newIndex))
        }
        this.isCrossDot(pos)
        this.control!.setDotPos(pos, this.focusDotIndex)
        this.syncValueByPos()
      }
    },
    getPosByEvent(e: MouseEvent | TouchEvent): number {
      return getPos(e, this.$el, this.isReverse, this.zoom)[this.isHorizontal ? 'x' : 'y'] / this.scale
    },
    renderSlot<T>(name: string, data: T, defaultSlot: any): any {
      const scopedSlot = this.$slots[name]
      return scopedSlot ? scopedSlot(data) : defaultSlot
    }
  },
  created() {
    this.initControl()
  },
  mounted() {
    this.bindEvent()
  },
  beforeUnmount() {
    this.unbindEvent()
  },
})
