@import url('https://fonts.googleapis.com/css2?family=Roboto&display=swap');


<template>
  <div class="container">
    <div style="display: flex">
      <!-- 选择原生的 prompt 部分  -->
<!--      <el-button @click="drawer = true" type="primary" style="margin-top: 4em; margin-left: 5em;"-->
<!--                 v-if="!selectContent">-->
        <el-button @click="drawer = true" type="primary" style="margin-top: 4em; margin-left: 5em; margin-right: 1em">
<!--        Select Prompts-->
        System Prompts
      </el-button>

<!--      <div v-else style=" display: flex">-->
      <div v-if="selectContent" style=" display: flex">
        <!-- 选择完毕之后出现可编辑的框 -->
        <div v-if="!p_edit" style="width: 500px; padding-left: 2em; padding-top: 2em; padding-right: 2em; border: 3px solid lightskyblue">{{selectContent}}</div>
        <div v-else id="editPromptBox" v-html="selectContent"
             style="width: 500px; padding-left: 2em; padding-top: 2em; padding-right: 2em"
             :contenteditable="p_edit">
        </div>

          <!--点击 "编辑"， 或者 "确定"来完成编辑-->
        <el-button @click="editCurPrompt" type="primary" class="button"
                   style="width: 14%; height: 40px; margin-top: 4em; margin-left: 1em">edit Prompt</el-button>
        <el-button @click="cancelPromptChange" type="primary" class="button"
                   style="width: 14%; height: 40px; margin-top: 4em;"
                   :disabled="!p_edit">cancel Edit</el-button>
        <el-button @click="savePromptChange" type="primary" class="button"
                   style="width: 14%; height: 40px; margin-top: 4em; margin-right: 1em" :disabled="!p_edit">confirm Edit</el-button>

      </div>

      <!-- 获得全部我自己的 prompt 记录-->
      <el-button type="primary" class="button" @click="getMyPrompt"
                 style="width: 12%; height: 40px; margin-top: 4em; margin-right: 4em">
        <i class="el-icon-user-solid"></i>
        My Prompts</el-button>
    </div>


      <el-button-group class="header">
        <el-dropdown v-model="selectedItem">
          <el-button type="primary">
            {{ selectedItem }}<i class="el-icon-arrow-down el-icon--right"></i>
          </el-button>
          <el-dropdown-menu slot="dropdown">
            <el-dropdown-item v-for="(item, index) in models" :key="index" @click.native="selectItem(item)">
              {{ item }}
            </el-dropdown-item>
          </el-dropdown-menu>
        </el-dropdown>

        <el-button @click="getWrtingAssistantHelp" type="primary" style="margin-left: 16px; margin-right: 50%">submit</el-button>

<!--        <el-button type="primary"  class="button" @click="startEdit">edit Output</el-button>-->
<!--        <el-button type="primary"  class="button" @click="saveChange">confirm Edit</el-button>-->
<!--        <el-button @click="copyText" type="primary">Copy Output</el-button>-->
        <!--        <el-button type="primary"  class="button" @click="refreshDrawer">重选</el-button>-->


        <el-drawer
              title="Select Proper Prompt"
              :visible.sync="drawer"
              :direction="direction"
              :size="'100%'">
            <el-menu>
              <el-menu-item v-for="(item, index) in prompts" :key="index"
                            @click.native="selectContent_(item.content)" class="drop-item">
<!--                {{item.content}}-->
                <el-input
                    type="textarea"
                    :autosize="{ minRows: 3, maxRows: 4}"
                    :placeholder="item.content"
                    style="width: 95%"
                >
                </el-input>
              </el-menu-item>
            </el-menu>
        </el-drawer>

        <el-drawer
            title="Select Your Prompt"
            :visible.sync="my_drawer"
            :direction="direction"
            :size="'90%'">
          <el-menu>
            <el-menu-item v-for="(item, index) in my_prompts" :key="index"
                          @click.native="mySelectContent_(item.content)" class="drop-item">
              <el-input
                  type="textarea"
                  :autosize="{ minRows: 3, maxRows: 4}"
                  :placeholder="item.content"
                   style="width: 95%"
                  >
              </el-input>
              <el-popconfirm
                  title="这是一段内容确定删除吗？" @confirm="confirmDelete(item.content)">
                <i slot="reference" class="el-icon-close"
                   style="padding-top: 1em" @click.stop>
                </i>
              </el-popconfirm>
            </el-menu-item>
          </el-menu>
        </el-drawer>

      </el-button-group>

  <div class="translate-container">
      <el-row :gutter=0>

        <!--output 部分-->
          <div class="grid-content-post bg-light" v-loading="loading">
            <div class="fr-view u-clearfix u-hover-feature u-rich-text u-text u-text-1" data-animation-name="" data-animation-duration="0" data-animation-direction="">
                  <h3 style="text-align: left;">
                    <span style="font-size: 2.5rem;">I’ve wasted so much time being depressed, that I’m depressed about wasting time.<br>
                    </span>
                  </h3>
                  <p id="isPasted" style="font-size: 1.5rem;">I can’t imagine how many days I have wasted to being sad and crying and not being able to accomplish the things I need to do. I have responsibilities I have deadlines and none of them seem to matter when I am in these dark episodes. How can you force yourself to do what needs to be done when you feel so hopeless and numb?</p>
                  <p>
                    <br><br><br>
                  </p>
                </div>
          </div>

      </el-row>

    </div>

    <!-- 核心部件-->
    <div class="translate-container">
      <el-row :gutter=20>
        <!--input 部分-->
        <el-col :span="12">
          <div class="grid-content bg-light">
            <label style="font-family: Roboto,RobotoDraft,Helvetica,Arial,sans-serif; font-size: large">Input</label>
            <p style="font-size: small; color: grey;">Note: Use gpt-3.5-turbo-16k only when the word count exceeds 5000.</p>
            <textarea id="inputArea" ref="markdownEditor" class="textarea"></textarea>
          </div>
        </el-col>

        <!--output 部分-->
        <el-col :span="12">
          <div class="grid-content bg-light" v-loading="loading">
            <div class="label_container" style="display: flex">
              <label style="font-family: Roboto,RobotoDraft,Helvetica,Arial,sans-serif; font-size: large">Output</label>
              <el-button @click="copyText" type="primary" style="margin-left: 30%; margin-bottom: 2em">Copy Output</el-button>
            </div>
            <textarea id="outputArea" ref="markdownEditorOutput" class="textarea"></textarea>
          </div>

        </el-col>
      </el-row>

    </div>



  </div>
</template>

<script>
/* eslint-disable */

import http from "@/http";
import VueMarkdown from "vue-markdown";
import EasyMDE from "easymde";
import "easymde/dist/easymde.min.css";


export default {
  name: "WritingAssitant",
  components:{
    VueMarkdown,
    EasyMDE,
  },


  mounted() {
    fetch('/prompt.json').then(response => response.json()).then(data => {
          this.prompts = data.prompts;
        }).catch(error => {
          console.log(error);
        });

    this.enableEditMode()
    this.enableOutputEditMode()

  //  检查是否是登录用户


  },
  data(){
    return {
      testData: '',
      edit:false,
      p_edit: false,

      markdownContent:'',
      prompts:null,
      my_prompts: null,
      drawer: false,
      my_drawer: false,
      direction: 'rtl',
      selectContent: '',
      beforeEdit: '',
      inputText: '',
      outputText: '',
      // models:['gpt-3.5-turbo', 'gpt-3.5-turbo-16k', 'gpt-4'],
      models:['gpt-3.5-turbo', 'gpt-3.5-turbo-16k'],
      selectedItem: 'gpt-3.5-turbo',


      editMode: true,
      m_outputText: "",
      markdownEditor: null,

      markdownOutputEditor: null,

      loading: false,
      value: 20,  

    }
  },

  methods:{
    jumpyTo(url){
      if(this.$router.currentRoute.path !== url){
        this.$router.push(url)
      }
    },

    messagePop(type, message){
      this.$message({
        message: `${message}`,
        type: `${type}`
      });
    },

    enableEditMode(){
      // this.editMode = true;
      const vm = this
      this.$nextTick(() => {
        if (!this.markdownEditor) {
          this.markdownEditor = new EasyMDE({
            element: this.$refs.markdownEditor,
          });

          //设计样式
          // const codemirrorScroll = document.getElementsByClassName('CodeMirror-scroll')[0];
          const codemirrorScroll = document.getElementsByClassName('CodeMirror-scroll')[0];
          console.log("scoll")
          console.log(codemirrorScroll)
          if (codemirrorScroll) {
            codemirrorScroll.style.maxHeight = '400px';
            codemirrorScroll.style.overflowY = 'auto';
            console.log("successfully set")
          }

          this.markdownEditor.codemirror.on("change", () => {
            vm.inputText = vm.markdownEditor.value();
            console.log(vm.inputText); // 输出当前的输入值
          });
        }
      });
    },

    enableOutputEditMode(){
      const vm = this
      this.$nextTick(() => {
        if (!this.markdown) {
          this.markdownOutputEditor = new EasyMDE({
            element: this.$refs.markdownEditorOutput,
          });
          this.markdownOutputEditor.codemirror.setOption('readOnly', true);
          //设计样式
          const codemirrorScroll = document.getElementsByClassName('CodeMirror-scroll')[1];
          if (codemirrorScroll) {
            codemirrorScroll.style.maxHeight = '400px';
            codemirrorScroll.style.overflowY = 'auto';
            console.log("successfully set")
          }

          // this.markdownOutputEditor.codemirror.on("change", () => {
          //   // vm.outputText = vm.markdownOutputEditor.value();
          //   //每次 vue-diff 的值也会跟着变
          //   this.$set(this, 'outputText', this.markdownOutputEditor.value());
          //   console.log(vm.outputText); // 输出当前的输入值
          // });
        }
      });
    },


    copyText(){
      this.$set(this, 'inputText', this.outputText);
      // this.inputText = this.outputText
      // document.getElementById('inputArea').textContent = this.outputText
      this.markdownEditor.value(this.outputText)
    },
    async confirmDelete(content){
      await http.post('/delete_prompt/',{
        content: content,
        person: sessionStorage.getItem('username')
      }).then(res=>{
        console.log("delete successfully")
        this.my_prompts = res.data.prompts
      }).catch(e=>{
        console.log(e)
      })
    },
    async savePromptChange(){
      console.log("触发")
      this.selectContent = document.getElementById('editPromptBox').textContent
      document.getElementById('editPromptBox').contentEditable = false
      this.p_edit = false
      //  加入到 drawer 自己修改的 prompt 中
        await this.saveMyPrompt()
    },

    startEdit(){

      this.edit = true;
      this.$nextTick(() => {
        let output = document.getElementById('outputArea');
        output.textContent = this.outputText;
      });

    },
    saveChange(){
      this.outputText = document.getElementById('outputArea').textContent
      // document.getElementById('outputArea').blur();
      document.getElementById('outputArea').contentEditable = false
      this.edit = false

      this.$message({
        message: 'successfully save changes',
        type: 'success'
      });
    },

    selectItem(content){
      this.selectedItem = content
    },
    selectContent_(content){
      this.selectContent = content
      this.beforeEdit = content
      this.drawer = false
    },

    editCurPrompt(){
      this.p_edit = true;
      this.$nextTick(() => {
        let output = document.getElementById('editPromptBox');
        output.textContent = this.selectContent;
      });
    },

    cancelPromptChange(){
      // this.p_edit = false
      // document.getElementById('editPromptBox').contentEditable = false
      this.$nextTick(() => {
        let output = document.getElementById('editPromptBox');
        this.p_edit = false
        document.getElementById('editPromptBox').contentEditable = false
        output.textContent = this.beforeEdit;
      });
    },

    mySelectContent_(content){
      this.selectContent = content
      console.log(this.selectContent)
      this.beforeEdit = content
      this.my_drawer = false
    },

    async saveMyPrompt(){
      http.post('/save_prompt/',{
        content: this.selectContent,
        person: sessionStorage.getItem('username')
      }).then(res=>{
        console.log("save prompt res", res)
        if (res.data.success){
          this.messagePop('success', "save prompt changes successfully!")
          console.log("save successfully")
        }
        else {
          this.messagePop('error', 'fail to save prompt changes')
        }
      }).catch(e=>{
        console.log(e)
        this.messagePop('error', 'fail to save prompt changes')
      })
    },

    async getMyPrompt(){
      this.my_drawer = true
      http.post('/get_prompt/',{
        person: sessionStorage.getItem('username')
      }).then(res=>{
        console.log("get successfully")
        this.my_prompts = res.data.prompts
      }).catch(e=>{
        console.log(e)
      })
    },

    getWrtingAssistantHelp(){
      this.loading = true
      let prompt = `[Paragraph]\n
                    ${this.inputText}\n
                    [TASK]\n
                    ${this.selectContent}\n
                    [RESULT]\n\n`
      console.log("提交的数据是：", prompt)
      http.post('/writing_assistant/',{
        prompt: prompt,
        model: this.selectedItem,
      }).then(res=>{
        this.loading = false
        this.outputText = res.data.content
        this.markdownOutputEditor.value(this.outputText)
        console.log("output Text:", res.data)
      }).catch(e=>{
        console.log(e)
      })
    },

    refreshDrawer(){
      this.drawer = true
    }
  },
}
</script>

