        <b-col align-v="center">
          <b-row align-self="center"
            ><center>
              <p>Please first submit the survey then go to the next page.</p>
            </center></b-row
          >

          <b-row align-self="center"
            ><center>
              <el-button @click="submitWriting" type="primary"
                >Next page</el-button
              >
            </center></b-row
          >
        </b-col>
