import "bootstrap-vue/dist/bootstrap-vue.css";

// Make BootstrapVue available throughout your project
Vue.use(BootstrapVue);

export default {
  name: "WritingAssitant",
  components: {
    VueMarkdown,
    EasyMDE,
    VueSimpleRangeSlider,
  },

  mounted() {
    fetch("/prompt.json")
      .then((response) => response.json())
      .then((data) => {
        this.prompts = data.prompts;
      })
      .catch((error) => {
        console.log(error);
      });

    this.enableEditMode();
    this.enableEditMode();

    this.enableOutputEditMode();

    //  检查是否是登录用户
  },
  data() {
    return {
      testData: "",
      edit: false,
      p_edit: false,
      sliderBool: !false,
      generateBool: true,
      markdownContent: "",
      prompts: null,
      my_prompts: null,
      drawer: false,
      my_drawer: false,
      direction: "rtl",
      stylePrompt:
        'Given two paragraphs, please summary the writer\'s writing style of message and give three adjective words or a phrase.\
        Respond using the following JSON format (In your summary, use single quotation marks \' instead of double quotation marks “. DON\'T USE double quotation marks):\n\
        {\n\
        "Summary": "[Summary]",\n\
        "Style1": "[Style1]",\n\
        "Style2": "[Style2]",\n\
        "Style3": "[Style3]"\n\
        }',

      mimicPrompt:
        "Based on two paragraphs, mimic the writer's writing style to generation the support message of following post: \
        I failed at every aspect of life\n\
        Declining health. couldn't complete my degree. never been in a relationship, lost contact with all my friends. probably gonna get fired from the job soon.\
        I turn 25 this year. I've lived liked this since I was a teenager. I gave up on life before it even started. I could have changed all of this but I didn't. I was too much of a coward to do so. Now its too late.\
        There is nothing to look forward to. I'm never getting married. I can't go visit another country. I'm gonna rot like this until the day that I die.\
        If I don't kms loneliness will. How can one person fuck up this bad? Not just once but multiple times?",
      beforeEdit: "",
      inputText: "",
      inputText2: "",

      outputText: "",
      // models:['gpt-3.5-turbo', 'gpt-3.5-turbo-16k', 'gpt-4'],
      models: ["gpt-3.5-turbo", "gpt-3.5-turbo-16k"],
      selectedItem: "gpt-3.5-turbo",

      editMode: true,
      m_outputText: "",
      markdownEditor: null,
      markdownEditor2: null,
      markdownOutputEditor: null,

      loading: false,
      value1: 50,
      value2: 50,
      value3: 50,
      style1: "clearness",
      style2: "formal",
      style3: "positive",

      currentTab: "Home",
      tabs: ["Home", "Posts", "Archive"],
    };
  },

  methods: {
    jumpyTo(url) {
      if (this.$router.currentRoute.path !== url) {
        this.$router.push(url);
      }
    },

    messagePop(type, message) {
      this.$message({
        message: `${message}`,
        type: `${type}`,
      });
    },

    enableEditMode() {
      this.editMode = true;
      const vm = this;
      this.$nextTick(() => {
        if (!this.markdownEditor) {
          this.markdownEditor = new EasyMDE({
            element: this.$refs.markdownEditor,
          });

          //设计样式
          // const codemirrorScroll = document.getElementsByClassName('CodeMirror-scroll')[0];
          const codemirrorScroll =
            document.getElementsByClassName("CodeMirror-scroll")[0];
          console.log("scoll");
          console.log(codemirrorScroll);
          if (codemirrorScroll) {
            codemirrorScroll.style.maxHeight = "400px";
            codemirrorScroll.style.overflowY = "auto";
            console.log("successfully set");
          }

          this.markdownEditor.codemirror.on("change", () => {
            vm.inputText = vm.markdownEditor.value();
            console.log(vm.inputText); // 输出当前的输入值
          });
        }
        if (!this.markdownEditor2) {
          this.markdownEditor2 = new EasyMDE({
            element: this.$refs.markdownEditor2,
          });

          //设计样式
          // const codemirrorScroll = document.getElementsByClassName('CodeMirror-scroll')[0];
          const codemirrorScroll =
            document.getElementsByClassName("CodeMirror-scroll")[0];
          console.log("scoll");
          console.log(codemirrorScroll);
          if (codemirrorScroll) {
            codemirrorScroll.style.maxHeight = "400px";
            codemirrorScroll.style.overflowY = "auto";
            console.log("successfully set");
          }

          this.markdownEditor2.codemirror.on("change", () => {
            vm.inputText2 = vm.markdownEditor2.value();
            console.log(vm.inputText2); // 输出当前的输入值
          });
        }
      });
    },

    enableOutputEditMode() {
      const vm = this;
      this.$nextTick(() => {
        if (!this.markdown) {
          this.markdownOutputEditor = new EasyMDE({
            element: this.$refs.markdownEditorOutput,
          });
          this.markdownOutputEditor.codemirror.setOption("readOnly", true);
          //设计样式
          const codemirrorScroll =
            document.getElementsByClassName("CodeMirror-scroll")[1];
          if (codemirrorScroll) {
            codemirrorScroll.style.maxHeight = "400px";
            codemirrorScroll.style.overflowY = "auto";
            console.log("successfully set");
          }

          // this.markdownOutputEditor.codemirror.on("change", () => {
          //   // vm.outputText = vm.markdownOutputEditor.value();
          //   //每次 vue-diff 的值也会跟着变
          //   this.$set(this, 'outputText', this.markdownOutputEditor.value());
          //   console.log(vm.outputText); // 输出当前的输入值
          // });
        }
      });
    },

    copyText() {
      this.$set(this, "inputText", this.outputText);
      // this.inputText = this.outputText
      // document.getElementById('inputArea').textContent = this.outputText
      this.markdownEditor.value(this.outputText);
    },
    async confirmDelete(content) {
      await http
        .post("/delete_prompt/", {
          content: content,
          person: sessionStorage.getItem("username"),
        })
        .then((res) => {
          console.log("delete successfully");
          this.my_prompts = res.data.prompts;
        })
        .catch((e) => {
          console.log(e);
        });
    },
    async savePromptChange() {
      console.log("触发");
      this.stylePrompt = document.getElementById("editPromptBox").textContent;
      document.getElementById("editPromptBox").contentEditable = false;
      this.p_edit = false;
      //  加入到 drawer 自己修改的 prompt 中
      await this.saveMyPrompt();
    },

    async showSlider() {
      this.sliderBool = true;
      //  加入到 drawer 自己修改的 prompt 中
      // await this.saveMyPrompt();
    },

    startEdit() {
      this.edit = true;
      this.$nextTick(() => {
        let output = document.getElementById("outputArea");
        output.textContent = this.outputText;
      });
    },
    saveChange() {
      this.outputText = document.getElementById("outputArea").textContent;
      // document.getElementById('outputArea').blur();
      document.getElementById("outputArea").contentEditable = false;
      this.edit = false;

      this.$message({
        message: "successfully save changes",
        type: "success",
      });
    },

    selectItem(content) {
      this.selectedItem = content;
    },
    selectContent_(content) {
      this.selectContent = content;
      this.beforeEdit = content;
      this.drawer = false;
    },

    editCurPrompt() {
      this.p_edit = true;
      this.$nextTick(() => {
        let output = document.getElementById("editPromptBox");
        output.textContent = this.selectContent;
      });
    },

    cancelPromptChange() {
      // this.p_edit = false
      // document.getElementById('editPromptBox').contentEditable = false
      this.$nextTick(() => {
        let output = document.getElementById("editPromptBox");
        this.p_edit = false;
        document.getElementById("editPromptBox").contentEditable = false;
        output.textContent = this.beforeEdit;
      });
    },

    mySelectContent_(content) {
      this.selectContent = content;
      console.log(this.selectContent);
      this.beforeEdit = content;
      this.my_drawer = false;
    },

    async saveMyPrompt() {
      http
        .post("/save_prompt/", {
          content: this.selectContent,
          person: sessionStorage.getItem("username"),
        })
        .then((res) => {
          console.log("save prompt res", res);
          if (res.data.success) {
            this.messagePop("success", "save prompt changes successfully!");
            console.log("save successfully");
          } else {
            this.messagePop("error", "fail to save prompt changes");
          }
        })
        .catch((e) => {
          console.log(e);
          this.messagePop("error", "fail to save prompt changes");
        });
    },

    async getMyPrompt() {
      this.my_drawer = true;
      http
        .post("/get_prompt/", {
          person: sessionStorage.getItem("username"),
        })
        .then((res) => {
          console.log("get successfully");
          this.my_prompts = res.data.prompts;
        })
        .catch((e) => {
          console.log(e);
        });
    },

    getWrtingAssistantHelp() {
      this.sliderBool = true;
      this.loading = true;
      // let prompt = `[Paragraph]\n
      //               ${this.inputText}\n
      //               [TASK]\n
      //               ${this.selectContent}\n
      //               [RESULT]\n\n`;
      let prompt = `[Paragraph1]\n
                    ${this.inputText}\n
                    [Paragraph2]\n
                    ${this.inputText2}\n
                    [TASK]\n
                    ${this.stylePrompt}\n
                    [RESULT]\n\n`;
      console.log("提交的数据是：", prompt);
      http
        .post("/writing_assistant/", {
          prompt: prompt,
          model: this.selectedItem,
        })
        .then((res) => {
          this.loading = false;
          this.outputText = res.data.content.Summary;
          this.style1 = res.data.content.Style1;
          this.style2 = res.data.content.Style2;
          this.style3 = res.data.content.Style3;
          // this.markdownOutputEditor.value(this.outputText);
          console.log("output Text:", res.data);
        })
        .catch((e) => {
          console.log(e);
        });
    },

    getWrtingAssistantMimic() {
      this.generateBool = true;
      this.loading = true;
      let prompt = `[Paragraph1]\n
                    ${this.inputText}\n
                    [Paragraph2]\n
                    ${this.inputText2}\n
                    [TASK]\n
                    ${this.mimicPrompt}\n
                    [RESULT]\n\n`;

      console.log("提交的数据是：", prompt);
      http
        .post("/writing_mimic/", {
          prompt: prompt,
          model: this.selectedItem,
        })
        .then((res) => {
          this.loading = false;
          this.outputText = res.data.content.Summary;
          this.style1 = res.data.content.Style1;
          this.style2 = res.data.content.Style2;
          this.style3 = res.data.content.Style3;
          // this.markdownOutputEditor.value(this.outputText);
          console.log("output Text:", res.data);
        })
        .catch((e) => {
          console.log(e);
        });
    },

    refreshDrawer() {
      this.drawer = true;
    },
  },
};
</script>

