/* eslint-disable */
import VueRouter from 'vue-router'
import DropBox from "@/components/DropBox";
import MainPage from "@/components/MainPage";
import FunctionSelection from "@/components/FunctionSelection";
import WritingAssitant from "@/components/WritingAssitant";
import SingleFileAssistant from "@/components/SingleFileAssistant";
import SingleFileUpload from "@/components/SingleFileUpload";
import LoginPage from "@/components/LoginPage";

const router = new VueRouter({
    routes: [{
            path: "/",
            // component: FunctionSelection
            component: LoginPage
        },
        {
            path: "/login",
            component: LoginPage
        },

        {
          path: "/function_selection",
          component: FunctionSelection,
        },

        {
            path: "/mainPage",
            component: MainPage,
        },

        {
            path: "/upload",
            component: DropBox
        },

        {
            path:"/writing_assistant",
            component:WritingAssitant
        },

        {path: '/single_file_assistant',
            component: SingleFileAssistant
        },

        {path: '/single_file_upload',
            component: SingleFileUpload
        },
    ]
});

//路由守卫，守卫 writing_assistant
router.beforeEach((to, from, next) => {
    if (to.path === '/writing_assistant') {   // 如果即将进入的是 '/writing_assistant' 路由
        const sessionData = sessionStorage.getItem('username');   // 通过 'yourItem' 取得 sessionStorage 中的项

        if (!sessionData) {   // 如果该项不存在
            next('/login');   // 重定向到登录页面
        } else {   // 如果该项存在
            next();   // 正常跳转
        }
    } else {   // 对于其它路由
        next();   // 总是允许正常跳转
    }
});


export default router;
