          id="outputArea"
          ref="enableEditMode"
          class="textarea"
        ></textarea>
        <!-- <b-row align="center">
          <el-button @click="getRegenerateMimic" type="primary"
            >Regenerate</el-button
          >
        </b-row> -->

        <el-button
          @click="submitWriting"
          type="primary"
          style="margin-left: 16px; margin-right: 50%"
          >Submit</el-button
        >
      </div>
      <div v-show="surveyShow">
        <iframe ref="Iframe" width="100%" height="1300px"></iframe>
        <center>
        <el-button
          @click="nextpage"
          type="primary"
          style="margin-left: 16px; margin-right: 50%"
          >next page</el-button
        ></center>
      </div>
    </div>
  </div>
</template>

<script>
/* eslint-disable */

import http from "@/http";
import VueMarkdown from "vue-markdown";
import EasyMDE from "easymde";
import "easymde/dist/easymde.min.css";
import VueSimpleRangeSlider from "vue-simple-range-slider/vue2";
import "vue-simple-range-slider/vue2/css";
import Vue from "vue";
import { BootstrapVue, IconsPlugin } from "bootstrap-vue";

// Import Bootstrap and BootstrapVue CSS files (order is important)
import "bootstrap/dist/css/bootstrap.css";
import "bootstrap-vue/dist/bootstrap-vue.css";

// Make BootstrapVue available throughout your project
Vue.use(BootstrapVue);

export default {
  name: "EditWriting",
  components: { EasyMDE },

  mounted() {
    this.fetchData();
    this.enableEditMode();
    this.cannotGoback();
  },
  data() {
    return {
      text: "",
      currentTask: 0,
      Pid: "",
      loading: true,
      surveyShow: false,
    };
  },

  methods: {
    cannotGoback() {
      window.onpopstate = function () {
        history.go(1);
      };
    },
    jumpyTo(url) {
      if (this.$router.currentRoute.path !== url) {
        this.$router.push({
          path: url,
          query: { username: this.Pid },
        });
      }
    },
    messagePop(type, message) {
      this.$message({
        message: `${message}`,
        type: `${type}`,
      });
    },
    fetchData() {
      console.log("Params: ", this.$route.query.currentTask);
      console.log("Params: ", this.$route.query.Pid);
      this.currentTask = this.$route.query.currentTask;
      this.Pid = this.$route.query.Pid;
      const iframe = this.$refs.Iframe;
      iframe.src =
        "https://nus.syd1.qualtrics.com/jfe/form/SV_1RmdtNoQdRouHie?username=" +
        this.Pid;
      http
        .post("/get_google_sheet_mimic_task/", {
          pid: this.Pid,
          currentTask: this.currentTask,
        })
        .then((res) => {
          this.loading = false;
          this.markdownEditor.value(res.data.mimic_output);
          console.log("mimic_output", res.data.mimic_output);
        })
        .catch((e) => {
          console.log(e);
        });
    },
    enableEditMode() {
      this.editMode = true;
      const vm = this;
      this.$nextTick(() => {
        if (!this.markdownEditor) {
          this.markdownEditor = new EasyMDE({
            element: this.$refs.markdownEditor,
          });

          //设计样式
          // const codemirrorScroll = document.getElementsByClassName('CodeMirror-scroll')[0];
          const codemirrorScroll =
            document.getElementsByClassName("CodeMirror-scroll")[0];
          console.log("scoll");
          console.log(codemirrorScroll);
          if (codemirrorScroll) {
            codemirrorScroll.style.maxHeight = "400px";
            codemirrorScroll.style.overflowY = "auto";
            console.log("successfully set");
          }
          this.markdownEditor.value(this.text);
          this.markdownEditor.codemirror.on("change", () => {
            vm.text = vm.markdownEditor.value();
            console.log(vm.text); // 输出当前的输入值
          });
        }
      });
    },
    submitWriting() {
      http
        .post("/upload_google_sheet_edited_task/", {
          pid: this.Pid,
          currentTask: this.currentTask,
          editedText: this.text,
        })
        .then((res) => {
          this.text = res.data.success;
          console.log("success", res.data.success);
          this.messagePop("success", "Successfully submit!");
          this.surveyShow = true;
          // this.jumpyTo("/writing_assitant_show");
        })
        .catch((e) => {
          console.log(e);
        });
    },
    nextpage() {
      if (this.currentTask == 2) {
        this.jumpyTo("/post_survey")
      } else {
        this.jumpyTo("/writing_assitant_show");
      }
    },
  },
};
</script>

